import { useState, useEffect } from 'react';

function backToTopButton() {
  const [backToTopButton, setbackToTopButton] = useState(false);

  const scrollUp = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };
  return (
    <div>
      {backToTopButton && (
        <button
          style={{
            position: 'fixed',
            bottom: '50px',
            right: '50px',
            height: '50px',
            width: '50px',
            fontSize: '50px',
          }}
        >
          ^
        </button>
      )}
    </div>
  );
}

export default backToTopButton;
